mapper.update:
UPDATE TB_SLOT S 
   SET S.STATUS = 'D', S.MODIFIED_AT = NOW()
 WHERE S.SLOT_ID IN (
 	SELECT TEMP.SLOT_ID FROM (
	 	SELECT SLOT_ID 
	 	  FROM TB_SLOT X 
	 	 WHERE X.TYPE = #{type}
	 	   AND X.ACCOUNT = #{account}
	 	   AND X.STATUS = 'A'
	 	 ORDER BY X.CREATED_AT ASC 
	 	 LIMIT ${cnt}
	) TEMP
 )
